import React from 'react';

const initialState = {
    user: ""
}

const actions = {
    SET_USER: "SET_USER",
    RESET: "RESET"
}

function reducer(state, action) {
    switch (action.type) {
        case actions.SET_USER:
            return {...state, user: action.value};
        case actions.RESET:
            return {...state, ...initialState};
        default:
            return state;

    }
}

export const AppContext=React.createContext();
